function Download-File { param( [string]$Url, [string]$OutFile ) Write-Host " Скачиваем   $OutFile ..." -ForegroundColor Cyan try { Invoke-WebRequest -Uri $Url -OutFile $OutFile -UseBasicParsing -ErrorAction Stop Write-Host " Успешно скачано." -ForegroundColor Green } catch { Write-Host "  Ошибка при загрузке: $_" -ForegroundColor Red exit 1 } }
