function Install-Silent { param( [string]$Installer, [string]$Args = "/S" ) if (Test-Path $Installer) { Write-Host "Установка $Installer ..." -ForegroundColor Yellow Start-Process -FilePath $Installer -ArgumentList $Args -Wait -ErrorAction Stop Write-Host "  Установка завершена." -ForegroundColor Green } else { Write-Host " Файл инсталлятора не найден: $Installer" -ForegroundColor Red exit 1 } }
Install-Silent -Installer $everythingInstaller Install-Silent -Installer $processHackerInstaller
